function createFile(name) {
  return {
    id: uuid(),
    name: name,
    size: 24,
    type: 'text/plain',
    content: 'data:text/plain;base64,VGhpcyBpcyBhIGR1bW15IGRvY3VtZW50',
  };
}

function createImage(name) {
  return {
    id: uuid(),
    name: name,
    size: 27564,
    type: 'image/jpeg',
    content:
      'data:image/jpeg;base64,',
  };
}

